import React from 'react';
import firebase from '../../FirebaseConfig';
import Cards from '../../components/Cards';

class Lobby extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      team_member: []
    };
  }

  render() {
    const team_member = this.state.team_member;
    return (
      <div className="App-header">
        <Cards cards={['0', '1/2', '1', '2', '3', '5', '8', '13', '21', '?', 'Coffe?']} />
        <ul>
          {
            team_member.map(({ name, story_point }) => (
              <li>
                <div>
                  <h1>{name}</h1>
                </div>
              </li>
            ))
          }
        </ul>
        <header className="App-header">
        </header>
      </div>
    );
  }

  getTeamMember = () => {
    let stringRef = this.props.location.state.room_name + '/team_member';
    let ref = firebase.database().ref(stringRef);
    ref.on('value', snapshot => {
      const data = snapshot.val();
      this.setState({team_member: []});
      if (data !== null) {
        data.map(({ name, story_point }) =>
          this.state.team_member.push({ name, story_point })
        );
      }
      console.log("member: " + this.state.team_member);
    });
  }

  joinRoom = () => {
    let stringRef = this.props.location.state.room_name + '/team_member';
    let ref = firebase.database().ref(stringRef);
    let listener = ref.on('value', snapshot => {
      ref.off('value', listener);
      const team_member = [];
      let data = snapshot.val();
      if (data !== null) {
        data.map(({ name, story_point }) => (team_member.push({ name, story_point })));
      }
      team_member.push({
        name: this.props.location.state.name,
        story_point: '',
      });
      const filterMembers = team_member.filter((member) => !team_member[member.name] && (team_member[member.name] = true));
      firebase.database().ref(stringRef).update(filterMembers);
    });
  }

  componentDidMount() {
    let prevState = this.props.location.state;
    if (prevState.status === 'join') {
      this.joinRoom();
    }
    this.getTeamMember();
  }

}

export default Lobby;
