import React from 'react';
import '../../App.css';
import banner from './banner.png';
import firebase from '../../FirebaseConfig';
import { GlobalStoreProvider, GlobalStore } from 'global-store-hook';

class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            loading: false,
            room_name: '',
            name: '',
            rooms: [],
        };
    }

    render() {
        return (
            <GlobalStoreProvider initValues={this.state}>
                <div className="App">
                    <header className="App-header">
                        <div>
                            <img src={banner} className="App-logo" alt="logo" />
                            <h1>Scrum Poker Online</h1>
                            <h1>Enter or Create a Room Here:</h1>
                            <form className="login-form" autoComplete="off" onSubmit={this.handleSubmit}>
                                <div className="form-row">
                                    <div style={{ marginTop: '13px' }}>
                                        <input type="text" ref='room_name' name='room_name' className="form-control" placeholder="Room Name" required onChange={this.handleInputChange} style={{ height: '32px', width: "370px" }} />
                                    </div>
                                    <div style={{ marginTop: '13px', marginBottom: '13px' }}>
                                        <input type="text" ref='name' name='name' className="form-control" placeholder="Name" required onChange={this.handleInputChange} style={{ height: '32px', width: "370px" }} />
                                    </div>
                                </div>
                                <button type="submit" className="btn btn-primary">Submit</button>
                            </form>
                        </div>
                    </header>
                </div>
            </GlobalStoreProvider>
        );
    }

    setGlobalValue = (name, value) => {
        let store = GlobalStore();
        store.set(name, value);
    }

    getData = () => {
        this.setState.loading = true;
        let ref = firebase.database().ref('/rooms/' + this.state.room_name);
        ref.on('value', snapshot => {
            let data = snapshot.val();
            console.log("ini room nya: " + this.state.room_name + "| ini datanya: " + JSON.stringify(data));
            if (data !== null && data['sessions'] === true && this.state.room_name !== '') {
                this.setState({ loading: false, rooms: snapshot.val() })
                // store.setState(this.state);
                this.setGlobalValue('room_name', this.state.room_name);
                this.props.history.push('/lobby');
            } else {
                alert('Create or Enter a room name first!');
            }
        })
    }

    handleSubmit = (event) => {
        event.preventDefault();
        this.getData();
    }

    handleInputChange = (event) => {
        const { name, value } = event.target;
        this.setState({ [name]: value.toLowerCase() });
    };

    componentDidMount() {
        if (this.state.room_name === '') {
            // Login first
            console.log('login dulu');
        } else {
            // Create room
            console.log('udah login');
            this.getData();
        }
    }

}

export default Login;
