import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import firebase from './FirebaseConfig';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      loading: false,
      room_name: '',
      rooms: [],
      messages: [],
    };
  }

  getData = () => {
    this.setState.loading = true;
    let ref = firebase.database().ref('/rooms/' + this.state.room_name);
    ref.on('value', snapshot => {
      this.setState({loading: false, rooms: snapshot.val()})
      console.log("Ini datanya: " + JSON.stringify(this.state.messages));
    })
  }

  componentDidMount() {
    if (this.state.room_name === '') {
      // Login first

    } else {
      // Create room
      this.getData();
    }
  }

  render() {
    return (
      <div className="container" style={{display: 'flex', justifyContent: 'left', margin: '30px'}}>
      <div className="row">
        <div className='col-xl-12'>
          <h1>Scrum Poker Online</h1>
          <h1>Enter or Create a Room Here:</h1>
          <form onSubmit={ this.handleSubmit }>
            <div className="form-row">
              <input type='hidden' ref='uid' />
              <div style={{marginTop: '13px'}}>
                <input type="text" ref='Room' className="form-control" placeholder="Room" />
              </div>
              <div style={{marginTop: '13px', marginBottom: '13px'}}>
                <input type="text" ref='name' className="form-control" placeholder="Name" />
              </div>
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
          </form>
        </div>
      </div>
    </div>
    );
  }
}

export default App;
