import React from 'react';
import firebase from '../../FirebaseConfig';
import Cards from '../../components/Cards';

const fibonacciCard = ['0', '1/2', '1', '2', '3', '5', '8', '13', '21', '?', 'Coffe?'];

class Lobby extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      team_member: [],
      story: '',
      isCreator: false,
    };
  }

  render() {
    return (
      <div style={{ margin: '30px' }} >
        <h1>The story goes like this:</h1>
        <div className="App-header">
          {
            this.state.isCreator ? (
              <div style={{ marginTop: '13px' }}>
                <input type="text" ref='user_story' name='user_story' className="form-control" placeholder="Your user story goes here" onChange={this.handleInputChange} style={{ height: '32px', width: "370px" }} />
              </div>
            ) : (
                <p>{this.state.story}</p>
              )
          }
          <ul>
            {
              this.state.team_member.map(({ name, story_point }) => (
                <li>
                  <div>
                    <p>asdlkjlsakdj</p>
                  </div>
                </li>
              ))
            }
          </ul>
          <Cards cards={fibonacciCard} />
        </div>
      </div>
    );
  }

  handleInputChange = (event) => {
    const { value } = event.target;
    this.updateUserStory(value);
  }

  updateUserStory = (user_story) => {
    let stringRef = this.props.location.state.room_name + '/story';
    firebase.database().ref(stringRef).update(user_story);
  }

  getData = () => {
    let ref = firebase.database().ref(this.props.location.state.room_name);
    ref.on('value', snapshot => {
      const team_member = snapshot.val()['team_member'];
      this.setState({ team_member: [], story: snapshot.val()['story'], isCreator: snapshot.val()['creator'] === this.props.location.state.name });
      if (team_member !== null) {
        team_member.map(({ name, story_point }) =>
          this.state.team_member.push({ name, story_point })
        );
        console.log(this.state.team_member);
      }
    });
  }

  joinRoom = () => {
    let stringRef = this.props.location.state.room_name + '/team_member';
    let ref = firebase.database().ref(stringRef);
    let listener = ref.on('value', snapshot => {
      ref.off('value', listener);
      const team_member = [];
      let data = snapshot.val();
      if (data !== null) {
        data.map(({ name, story_point }) => (team_member.push({ name, story_point })));
      }
      team_member.push({
        name: this.props.location.state.name,
        story_point: '',
      });
      const filterMembers = team_member.filter((member) => !team_member[member.name] && (team_member[member.name] = true));
      firebase.database().ref(stringRef).update(filterMembers);
    });
  }

  componentDidMount() {
    let prevState = this.props.location.state;
    if (prevState.status === 'join') {
      this.joinRoom();
    }
    this.getData();
  }

}

export default Lobby;
