import React from 'react';
import '../../App.css';
import banner from './banner.png';
import firebase from '../../FirebaseConfig';
import { GlobalStoreProvider, GlobalStore } from 'global-store-hook';

const Login = () => {
    let state = {
        loading: false,
        room_name: '',
        name: '',
        rooms: [],
    };

    const getData = () => {
        const store = GlobalStore();
        state.setState.loading = true;
        let ref = firebase.database().ref('/rooms/' + state.room_name);
        ref.on('value', snapshot => {
            let data = snapshot.val();
            console.log("ini room nya: " + state.room_name + "| ini datanya: " + JSON.stringify(data));
            if (data !== null && data['sessions'] === true && state.room_name !== '') {
                state.setState({ loading: false, rooms: snapshot.val() })
                // store.setState(state);
                this.props.history.push('/lobby');
            } else {
                alert('Create or Enter a room name first!');
            }
        })
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        getData();
    }

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        state.setState({ [name]: value.toLowerCase() });
    };

    // componentDidMount() {
    //     if (this.state.room_name === '') {
    //         // Login first
    //         console.log('login dulu');
    //     } else {
    //         // Create room
    //         console.log('udah login');
    //         this.getData();
    //     }
    // }

    return (
        <GlobalStoreProvider initValues={state}>
            <div className="App">
                <header className="App-header">
                    <div>
                        <img src={banner} className="App-logo" alt="logo" />
                        <h1>Scrum Poker Online</h1>
                        <h1>Enter or Create a Room Here:</h1>
                        <form className="login-form" autoComplete="off" onSubmit={handleSubmit}>
                            <div className="form-row">
                                <div style={{ marginTop: '13px' }}>
                                    <input type="text" ref='room_name' name='room_name' className="form-control" placeholder="Room Name" required onChange={handleInputChange} style={{ height: '32px', width: "370px" }} />
                                </div>
                                <div style={{ marginTop: '13px', marginBottom: '13px' }}>
                                    <input type="text" ref='name' name='name' className="form-control" placeholder="Name" required onChange={handleInputChange} style={{ height: '32px', width: "370px" }} />
                                </div>
                            </div>
                            <button type="submit" className="btn btn-primary">Submit</button>
                        </form>
                    </div>
                </header>
            </div>
        </GlobalStoreProvider>
    );
}

export default Login;
