import React from 'react';
import firebase from '../../FirebaseConfig';
import Cards from '../../components/Cards'

class Lobby extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      team_member: []
    };
  }

  render() {
    return (
      <div className="App">
        <Cards cards={['0', '1/2', '1', '2', '3', '5', '8', '13', '21', '?', 'Coffe?']} />
        <header className="App-header">
        </header>
      </div>
    );
  }

  getTeamMember = () => {
  }

  joinRoom = () => {
    let stringRef = this.props.location.state.room_name + '/team_member';
    let ref = firebase.database().ref(stringRef);
    let listener = ref.on('value', snapshot => {
      ref.off('value', listener);
      let data = snapshot.val();
      data.map(({ name, story_point }) => (this.state.team_member.push({ name, story_point })));
      this.state.team_member.push({
        name: this.props.location.state.name,
        story_point: '',
      });
      firebase.database().ref(stringRef).update(this.state.team_member);
    });
  }

  componentDidMount() {
    let prevState = this.props.location.state;
    if (prevState.status === 'join') {
      this.joinRoom();
    }
  }

}

export default Lobby;
