import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import firebase from './FirebaseConfig';
import { useHistory } from "react-router-dom";

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      loading: false,
      room_name: '',
      name: '',
      rooms: [],
      messages: [],
    };
  }

  getData = () => {
    this.setState.loading = true;
    let ref = firebase.database().ref('/rooms/' + this.state.room_name);
    ref.on('value', snapshot => {
      this.setState({loading: false, rooms: snapshot.val()})
      console.log("Ini datanya: " + JSON.stringify(this.state.rooms));
    })
  }

  handleSubmit = (event) => {
    event.preventDefault();
    let history = useHistory();
    let d_room_name = this.refs.room.value;
    let d_name = this.refs.name.value;
    this.setState({room_name: d_room_name, name: d_name})
    this.getData();
    history.push('/lobby');
  }

  componentDidMount() {
    if (this.state.room_name === '') {
      // Login first
      console.log('login dulu');
    } else {
      // Create room
      console.log('udah login');
      this.getData();
    }
  }

  render() {
    return (
      // <div className="App">
      //   <header className="App-header">
      //     <img src={logo} className="App-logo" alt="logo" />
      //     <p>
      //       Edit <code>src/App.js</code> and save to reload.
      //     </p>
      //     <a
      //       className="App-link"
      //       href="https://reactjs.org"
      //       target="_blank"
      //       rel="noopener noreferrer"
      //     >
      //       Learn React
      //     </a>
      //   </header>
      // </div>
      <div className="App">
      <header className="App-header">
        <div>
          <img src={logo} className="App-logo" alt="logo" />
          <h1>Scrum Poker Online</h1>
          <h1>Enter or Create a Room Here:</h1>
          <form onSubmit={ this.handleSubmit }>
            <div className="form-row">
              <div style={{marginTop: '13px'}}>
                <input type="text" ref='room' className="form-control" placeholder="Room" style={{height: '32px', width: "370px"}}/>
              </div>
              <div style={{marginTop: '13px', marginBottom: '13px'}}>
                <input type="text" ref='name' className="form-control" placeholder="Name" style={{height: '32px', width: "370px"}}/>
              </div>
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
          </form>
        </div>
      </header>
    </div>
    );
  }
}

export default App;
