import React from 'react';
import Card from '../Card';
import './Cards.css';
import firebase from '../../FirebaseConfig';

const Cards = ({ cards, room_name, name, sessions, status }) => (
  <div className="centerDiv">
    <ul className="cards">
      {
        cards.map((card) => (
          <li className="cards__card" key={card} onClick={(event) => handleClick(event, card)}>
            <Card card={card} />
          </li>
        ))
      }
    </ul>
  </div>
);

const handleClick = (event, point) => {
  event.preventDefault();
  console.log("coba click kartu: " + point);
};

export default Cards;
